import React, {Component} from 'react';
import { connect } from 'react-redux';
import { changeCheckedNote, changeUncheckedNote} from "../actions";
import './../style.css';


class Note extends Component {
    constructor(props){
        super(props);

        this.state = {checked: props.checked};

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = () => {
        if (this.props.checked)
            this.props.changeCheckedNote(this);
        else this.props.changeUncheckedNote(this);
    };

    // render() {
    //     return (
    //         <div className="Note">
    //             <input type="checkbox" className="NoteCheckbox" onChange={() => this.handleChange()}
    //                    defaultChecked={this.props.checked}/>
    //             <label className={`NoteText${this.props.checked}`}>{this.props.text}</label>
    //         </div>
    //     )
    // }

    render() {
        return (
            <div className="Note">
                <input type="checkbox" className="NoteCheckbox" onChange={() => this.handleChange()}
                       checked={this.state.checked}/>
                <label className={`NoteText${this.props.checked}`}>{this.props.text}</label>
            </div>
        )
    }
}

const mapStateToProps = () => {return {}};

export default connect(mapStateToProps, {changeCheckedNote, changeUncheckedNote})(Note);
